USE pos_iados;


-- PRODUCTO_TIENDA: vincular productos activos de tenant_id=1 a tienda_id=1

-- Verificacion rapida
SELECT 'Tenants:'      AS info, COUNT(*) AS total FROM tenants
UNION ALL SELECT 'Empresas:',   COUNT(*) FROM empresas   WHERE tenant_id=1
UNION ALL SELECT 'Tiendas:',    COUNT(*) FROM tiendas    WHERE tenant_id=1
UNION ALL SELECT 'Usuarios:',   COUNT(*) FROM users      WHERE tenant_id=1
UNION ALL SELECT 'Categorias:', COUNT(*) FROM categorias WHERE tenant_id=1 AND activo=1
UNION ALL SELECT 'Productos:',  COUNT(*) FROM productos  WHERE tenant_id=1 AND activo=1
UNION ALL SELECT 'Prod-Tienda:',COUNT(*) FROM producto_tienda WHERE tienda_id=1
UNION ALL SELECT 'Menu config:',COUNT(*) FROM menu_digital_config WHERE tenant_id=1;
